package com.bayamp.akshatha.training.utils;

public class AdditionUtils {

	public static int addnumbers(int[] myNumbers) {
		// TODO Auto-generated method stub
		
		int sum=0;
		for(int ele:myNumbers) {
			sum=sum+ele;
		}
		return sum;
	}
	
	public static int addTwoNumbers(int num1,int num2) {
		int total=num1+num2;
		
		return total;
	}

}
